var crypto=require('crypto');
var mongoose=require('DB/mongoose.js'),
    Schema=mongoose.Schema;

var schema=new Schema({
    id:{
        type:Number,
        required:true
    },
    users:{
        type:Array,
        default:[]
    },
    name:{
        type:String,
        default:"Комната 1488"
    },
    Messages:{
        type:Object,
        default:{}
    },
    private:{
        type:Boolean,
        default:true
    }
});


var room=mongoose.model('room',schema);
exports.Room=room;


