var crypto=require('crypto');
var road=require('GAME/ROAD.js');
var city=require("GAME/CITY.js");
var mongoose=require('DB/mongoose.js'),
    Schema=mongoose.Schema;

var schema=new Schema({
    ownerId:{
        type:String,
        required:true
    },
    id:{
        type:String,
        required:true,
        unique:true
    },
    name:{
        type:String,
        required:true,
        unique:true
    },
    lvl:{
        type:Number,
        default:1
    },
    curRoad:{
        type:Array,
        default:[]
    }
});
var Walker=mongoose.model('walker',schema);
exports.Walker=Walker;


